          <input matInput [formControl]="alias" placeholder="Alias">
          <mat-error *ngIf="alias.hasError('required')">Alias is required</mat-error>
          <mat-error *ngIf="alias.hasError('minlength')">Alias must be at least 5 characters long</mat-error>
          <mat-error *ngIf="alias.hasError('maxlength')">Alias can be max 25 characters long</mat-error>
        </mat-form-field>

        <mat-form-field appearance="outline" class="full-width">
          <mat-label>Birth Date</mat-label>
          <input matInput [matDatepicker]="picker" [formControl]="birth_date">
          <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
          <mat-datepicker #picker></mat-datepicker>
          <mat-error *ngIf="birth_date.hasError('required')">Birth date is required</mat-error>
        </mat-form-field>

        <mat-form-field appearance="outline" class="full-width">
          <mat-label>Email</mat-label>
          <input matInput type="email" [formControl]="email" placeholder="Email">
          <mat-error *ngIf="email.hasError('required')">Email is required</mat-error>
          <mat-error *ngIf="email.hasError('pattern')">Please enter a valid email address</mat-error>
        </mat-form-field>

        <mat-form-field appearance="outline" class="full-width">
          <mat-label>Password</mat-label>
          <input matInput type="password" [formControl]="password" placeholder="Password">
          <mat-error *ngIf="password.hasError('required')">Password is required</mat-error>
          <mat-error *ngIf="password.hasError('minlength')">Password must be at least 8 characters long</mat-error>
        </mat-form-field>

        <button mat-raised-button color="primary" type="submit" [disabled]="profileForm.invalid">Update Profile</button>
      </form>
    </mat-card-content>
  </mat-card>
</div>
