import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Store } from '@ngrx/store';
import { AppState } from 'src/app/app.reducers';
import * as CategoriesAction from '../../actions';
import { CategoryDTO } from '../../models/category.dto';

@Component({
  selector: 'app-category-form',
  templateUrl: './category-form.component.html',
  styleUrls: ['./category-form.component.scss'],
})
export class CategoryFormComponent implements OnInit {
  category: CategoryDTO;
  title: FormControl;
  description: FormControl;
  css_color: FormControl;

  categoryForm: FormGroup;
  isValidForm: boolean | null;

  isUpdateMode: boolean; // Changed from private to public

  private categoryId: string | null;
  private userId: string;

  constructor(
    private activatedRoute: ActivatedRoute,
    private formBuilder: FormBuilder,
    private store: Store<AppState>
  ) {
    this.userId = '';

    this.isValidForm = null;
    this.categoryId = this.activatedRoute.snapshot.paramMap.get('id');
    this.category = new CategoryDTO('', '', '');
    this.isUpdateMode = false;

    this.title = new FormControl(this.category.title, [
      Validators.required,
      Validators.maxLength(55),
    ]);

    this.description = new FormControl(this.category.description, [
      Validators.required,
      Validators.maxLength(255),
    ]);

    this.css_color = new FormControl(this.category.css_color, [
      Validators.required,
      Validators.maxLength(7),
    ]);

    this.categoryForm = this.formBuilder.group({
      title: this.title,
      description: this.description,
      css_color: this.css_color,
    });

    this.store.select('auth').subscribe((auth) => {
      if (auth.credentials.user_id) {
        this.userId = auth.credentials.user_id;
      }
    });

    this.store.select('categories').subscribe((categories) => {
      this.category = categories.category;

      this.title.setValue(this.category.title);

      this.description.setValue(this.category.description);

      this.css_color.setValue(this.category.css_color);

      this.categoryForm = this.formBuilder.group({
        title: this.title,
        description: this.description,
        css_color: this.css_color,
      });
    });
  }

  ngOnInit(): void {
    // update
    if (this.categoryId) {
      this.isUpdateMode = true;
      this.store.dispatch(
        CategoriesAction.getCategoryById({ categoryId: this.categoryId })
      );
    } else {
      this.categoryForm.reset();
    }
  }

  private editCategory(): void {
    if (this.categoryId) {
      if (this.userId) {
        this.category.userId = this.userId;

        this.store.dispatch(
          CategoriesAction.updateCategory({
            categoryId: this.categoryId,
            category: this.category,
          })
        );
      }
    }
  }

  private createCategory(): void {
    if (this.userId) {
      this.category.userId = this.userId;

      this.store.dispatch(
        CategoriesAction.createCategory({ category: this.category })
      );
    }
  }

  saveCategory(): void {
    this.isValidForm = false;

    if (this.categoryForm.invalid) {
      return;
    }

    this.isValidForm = true;
    this.category = this.categoryForm.value;

    if (this.isUpdateMode) {
      this.editCategory();
    } else {
      this.createCategory();
    }
  }
}
<div class="category-form-container">
  <mat-card>
    <mat-card-header>
      <mat-card-title>{{ isUpdateMode ? 'Edit Category' : 'Create Category' }}</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <form [formGroup]="categoryForm" (ngSubmit)="saveCategory()">
        <mat-form-field appearance="outline" class="full-width">
          <mat-label>Title</mat-label>
          <input matInput [formControl]="title" placeholder="Title">
          <mat-error *ngIf="title.hasError('required')">Title is required</mat-error>
          <mat-error *ngIf="title.hasError('maxlength')">Title can be max 55 characters long</mat-error>
        </mat-form-field>

        <mat-form-field appearance="outline" class="full-width">
          <mat-label>Description</mat-label>
          <textarea matInput [formControl]="description" placeholder="Description" rows="4"></textarea>
          <mat-error *ngIf="description.hasError('required')">Description is required</mat-error>
          <mat-error *ngIf="description.hasError('maxlength')">Description can be max 255 characters long</mat-error>
        </mat-form-field>

        <mat-form-field appearance="outline" class="full-width">
          <mat-label>CSS Color</mat-label>
          <input matInput [formControl]="css_color" placeholder="CSS Color (e.g., #FF5733)">
          <mat-error *ngIf="css_color.hasError('required')">CSS Color is required</mat-error>
          <mat-error *ngIf="css_color.hasError('maxlength')">CSS Color can be max 7 characters long</mat-error>
        </mat-form-field>

        <div class="color-preview" [style.background-color]="css_color.value"></div>

        <button mat-raised-button color="primary" type="submit" [disabled]="categoryForm.invalid">
          {{ isUpdateMode ? 'Update' : 'Create' }}
        </button>
      </form>
    </mat-card-content>
  </mat-card>
</div>
